<?php//Si l'utilisateur n'est pas loggedif ($_SESSION['logged'] == false) {	if(isset($_SESSION['activation']))	{		echo '<p id="'.$_SESSION['error'].'">';		echo $_SESSION['activation'];		unset($_SESSION['activation']);		unset($_SESSION['error']);		echo '</p>';			}else{	?>		<form method="POST" action="modules/membre/traitement/verif_activation.php" name="inscription" enctype="application/x-www-form-urlencoded">			<fieldset>				<legend> Code d'activation</legend>				<p>					<input name="code_activation" size='50' type="text" value="<?php if(isset($_GET['code_activation'])) echo $_GET['code_activation'];  ?>">					<input type="submit" name="Activation" value="Activation">				</p>			</fieldset>		</form>	<?php	}}else{	// Si l'utilisateur est déjà logged alors on lui dit	?> 		<p id='error'> Vous avez déjà un compte </p>	<?php}?>